class Solution {
public:
    vector<int> divisibilityArray(string s, int m) {
        long long num=0;
        vector<int> ans;
        for(auto i:s){
            num=num*10+i-'0';
            if(num%m==0){
                ans.push_back(1);
            }else ans.push_back(0);
            num%=m;
        }
        return ans;
    }
};
