class Solution {
public:
    int strStr(string haystack, string needle) {
        if(needle.length() > haystack.length())return -1;

        for(int i = 0; i < haystack.length() - needle.length() + 1;i++){
            int f = 0;
            for(int j = 0;j<needle.length();j++){
                if(haystack[i + j] != needle[j]){
                    f = 1;
                    break;
                }
            }
            if(f == 0){
                return i;
            }
        }
        return -1;        
    }
};
