class Solution {
public:
    int divisorSubstrings(int num, int k) {
        string str=to_string(num);
        int ans;
        for(int i=0;i<str.length();i++){
            string div;
            int p=k;
            int idx=i;
            while(p--&&idx<str.length()){
                div.push_back(str[idx]);
                idx++;
            }

            int dic=stoi(div);
            cout<<dic<<" ";
            if(dic&&num%dic==0&&div.length()==k){
                ans++;
            }
        }
        return ans;
    }
};
