class Solution {
public:
    static bool comp(string &a, string &b) {
        return (a.length() == b.length()) ? a < b : a.length() < b.length();
    }
    string kthLargestNumber(vector<string>& nums, int k) {
        sort(nums.begin(), nums.end(), comp);
        reverse(nums.begin(), nums.end());
        return nums[k - 1];

    }
};
