class Solution {
public:
    int longestSemiRepetitiveSubstring(string s) {
        int n = s.length(), ans = 0;
        for(int i = 0; i < n; i++){
            for(int j= i; j < n; j++){
                int t = 0;
                for(int k = i ; k < j; k++){
                    if(s[k] == s[k + 1]){
                        t++;
                    }
                }
                if(t <= 1)
                    ans = max(ans, j - i + 1);
            }
        }
        return ans;
    }
};
