class Solution {
public:
    vector<int> circularGameLosers(int n, int k) {
        map<int, int>  mp;
        for(int i = 0, j = 0; i < n;i++){
            j+=i * k;
            j%=n;
            mp[j]++;
            if(mp[j] > 1)break;
        }
        vector<int> ans;
        for(int i = 0; i < n; i++){
            if(mp[i] == 0){
                ans.push_back(i + 1);
            }
        }
        return ans;
    }
};
