class Solution {
public:
    vector<int> findThePrefixCommonArray(vector<int>& A, vector<int>& B) {
        map<int, int> mp1, mp2;
        int n = A.size();
        vector<int> temp(n, 0);
        for(int i = 0; i <n;i++){
            mp1[A[i]]++;
            mp2[B[i]]++;
            int c = 0;
            for(auto i : mp1){
                if(mp2[i.first] > 0)c++;
            }
            temp[i] = c;
        }
        return temp;
    }
};
