class Solution {
public:
    int smallestDivisor(vector<int>& nums, int threshold) {
        long long lo = 1, hi = 1e9;
        long long ans = 0;
        while(lo <= hi){
            long long mid = (lo + hi)/2;
            long long t = 0;
            for(auto i : nums){
                t += ceil((i * 1.0)/mid);
            }
            if(t <= threshold){
                ans = mid;
                hi = mid - 1;
            }else{
                lo = mid + 1;
            }
        }
        return ans;
    }
};
