class Solution {
public:
    int chalkReplacer(vector<int>& chalk, int k) {
        vector<long> pre(chalk.size());
        pre[0] = chalk[0];
        for(int i = 1; i < pre.size();i++)
            pre[i] += pre[i - 1] + chalk[i];
        k = k % pre.back();
        return  upper_bound(pre.begin(), pre.end(), k) - pre.begin();
    }
};
