class Solution {
public:
    vector<int> findColumnWidth(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        vector<int> arr;
        for(int j = 0; j < m;j++){
            int ans = 0;
            for(int i = 0; i < n;i++){
               ans = max(ans, (int)to_string(grid[i][j]).length());
            }
            arr.push_back(ans);
        }
        return arr;
    }
};
