class Solution {
public:
    int findTheWinner(int n, int k) {
        int cnt = 0;

        queue<int> q;
        for(auto i = 1; i <= n; i++) {
            q.push(i);
        }
        while(q.size() > 1) {
            int t = q.front();
            q.pop();
            cnt++;
            if(cnt == k) {
                cnt = 0;
            }
            else {
                q.push(t);
            }
        }
        return q.front();
    }
};
