class Solution {
public:
    vector<long long> sumOfThree(long long num) {
        vector<long long> ans;
        if((num - 3) % 3 != 0) {
            return ans;
        }
        long long t = (num - 3) / 3;
        ans.push_back(t);
        ans.push_back(t + 1);
        ans.push_back(t + 2);
        return ans;
    }
};
