class Solution {
public:
    int countCharacters(vector<string>& words, string chars){


        string ans = "";
        for(auto i : words){
            unordered_map<char,int> m;
            for(auto j : chars){
                m[j]++;

            }
            int cnt = 0;

            for(auto k : i){
                if(m[k] > 0){
                    m[k]--;
                }
                else{
                    cnt = 1;
                    break;
                }
            }
            if(cnt == 0){
                ans += i;
            }
        }
        return ans.length();


    }
};
