const int N = 1000000;
class Solution {
public:
    int arr[N];
    int firstMissingPositive(vector<int>& nums) {
        memset(arr,0,sizeof arr);
        for(auto i : nums){
            if(i< 0 || i> N) continue;
            arr[i] = 1;
        }
        for(int i=1;i<N;i++){
            if(arr[i] == 0){
                return i;
            }
        }
        return -1;
    }
};
