class Solution {
public:
    vector<int> d4 = {-1, 0, 1, 0, -1};
    void helper(vector<vector<int>> &img, int x, int y, int color, int prev, vector<vector<int>> &vis){
        vis[x][y] = 1;
        img[x][y] = color;
        for(int i = 0 ; i < 4;i++){
            int _x = x + d4[i];
            int _y = y + d4[i + 1];
            if(_x >=0 && _y>=0 && _x < img.size() && _y < img[0].size() && img[_x][_y] == prev && vis[_x][_y] != 1){
                helper(img, _x, _y, color, prev, vis);
            }
        }
        return;
    }
    vector<vector<int>> floodFill(vector<vector<int>>& image, int sr, int sc, int color) {
        vector<vector<int>> vis(image.size(), vector<int> (image[0].size(), 0));
        helper(image, sr, sc, color, image[sr][sc], vis);
        return image;
    }
};
