class Solution {
public:
    int minNumber(vector<int>& nums1, vector<int>& nums2) {
        map<int, int> mp1, mp2;
        for(auto i : nums1)mp1[i]++;

        for(auto i : nums2)mp2[i]++;
        for(auto i : mp1){
            if(i.second == mp2[i.first]){
                return i.first;
            }
        }
        sort(nums1.begin(), nums1.end());
        sort(nums2.begin(), nums2.end());
        if(nums1[0] > nums2[0]){
            return nums2[0] * 10 + nums1[0];
        }
        return nums1[0] * 10 + nums2[0];
    }
};
