class Solution {
public:
    int totalFruit(vector<int>& fruits) {
        map<int, int> m;
        int i = 0;
        int j = 0;
        int ans = 0;
        while(j < fruits.size()) {
            m[fruits[j]]++;
            if(m.size() <= 2) {
                ans = max(ans, j - i + 1);
            } 
            while(i <= j and m.size() > 2) 
            {
                m[fruits[i]]--;
                if(m[fruits[i]] == 0) {
                    m.erase(fruits[i]);
                }
                i++;

            }
            j++;
        }
        return ans;
    }
};
