class Solution {
public:
    int canCompleteCircuit(vector<int>& gas, vector<int>& cost) {
        int total = 0, tank = 0, ind = -1;
        for(int i = 0; i < cost.size();i++){
            total += gas[i] - cost[i];
        }           
        if(total < 0)return -1;
        int ans = 0;
        for(int i = 0; i < cost.size();i++){
            tank += gas[i] - cost[i];
            if(tank < 0){
                ans = i + 1;
                tank = 0;
            }
        }
        return ans;
    }
};
