class Solution {
public:
    string generateTheString(int n) {
        string ans="";
        if(n%2==0){
            while(n-1){
                ans+='a';
                n--;

            }
            ans+='b';

        }else{
            //string ans = ""
            while(n){
                ans+='a';
                n--;
            }

        }
        return ans;

    }
};
