class Solution {
public:
    void foo(int open, int close, string s, vector<string> &arr)
    {
        if (open == 0 && close == 0)
        {
            arr.push_back(s);
        }
        if (open != 0)
        {
            s.push_back('(');
            foo(open - 1, close, s, arr);
            s.pop_back();
        }

        if (open != close)
        {
            s.push_back(')');
            foo(open, close - 1, s, arr);
            s.pop_back();
        }
    }
    vector<string> generateParenthesis(int n)
    {
        vector<string> arr;
        foo(n, n, "", arr);
        return arr;
    }
};
