class Solution {
public:
    int getMaximumGenerated(int n) {
        int dp[n+1];
        if(n == 0){
            return 0;
        }
        dp[0] = 0;
        dp[1] = 1;
        int _max = 1;
        for(int i=2;i<=n;i++){
            if(i%2 == 0){
                dp[i] = dp[i/2];
            }else{
                dp[i] = dp[i/2] + dp[i/2 + 1];
            }
            _max = max(_max ,dp[i]);
        }
        return _max;
    }
};
