class Solution {
public:
    string interpret(string s) {
        string ans = "";
        for(int i = 0; i < s.length();i++){
            if(s[i] == '('){
                continue;
            }else if(s[i] == ')'){
                if(s[i-1] == '('){
                    ans.push_back('o');
                }else{
                    ans.append("al");
                }
            }else if(s[i] == 'G'){
                ans.push_back('G');
            }
        }
        return ans;
    }
};
