class Solution {
public:
    vector<int> grayCode(int n) {
        vector<int> ans = {0};
        for(int i = 1 ; i < pow(2, n); i++){
            int temp = 0;
            for(int j = 0; j < log2(i) + 1;j++){
                int curr = ((i & (1<<j)) == 0)? 0 : 1;
                int next = 0;
                if(j != log2(i))
                    next = ((i & (1<<(j + 1))) == 0)? 0 : 1;
                if((curr ^ next) == 1){
                    temp = (temp | (1<<j));
                }
            }
            ans.push_back(temp);
        }
        return ans;
    }
};
