class Solution {
public:
    int numSpecialEquivGroups(vector<string>& A) {
        set<string> s;
        for(auto node:A){

            string even = "",odd = "";
            for(int i=0;i<node.length();i++){
                if(i%2 == 0){
                    even += node[i];
                }
                else odd += node[i];
            }

            sort(even.begin(),even.end());
            sort(odd.begin(),odd.end());


            s.insert(even + odd);
        }

        return s.size();

    }
};
