class Solution {
public:
    int dp[205][205];
    int find(int l, int r) {
        if(l >= r) {
            return 0;
        }
        if(dp[l][r] != -1) {
            return dp[l][r];
        }
        int ans = INT_MAX / 2;
        for(int i = l; i <= r; i++) {
            ans = min(ans, i + max(find(l, i - 1),find(i + 1, r)));
        }
        return dp[l][r] =  ans;
    }
    int getMoneyAmount(int n) {
        memset(dp, -1, sizeof dp);
        return find(1, n);
    }
};
