/** 
 * Forward declaration of guess API.
 * @param  num   your guess
 * @return 	     -1 if num is higher than the picked number
 *			      1 if num is lower than the picked number
 *               otherwise return 0
 * int guess(int num);
 */

class Solution {
public:
    int guessNumber(int n) {
        int lo = 1, hi = n;
        while(1){
            int mid = lo  + (hi - lo)/2;
            int t = guess(mid);
            if(t == 0){
                return mid;
            }else if(t == 1){
                lo = mid + 1;
            }else{
                hi = mid - 1;
            }
        }
        return -1;
    }
};
