class Solution {
public:
    int hIndex(vector<int>& citations) {
        long lo = 0, hi = citations.size(), ans = 0;
        while(lo <= hi){
            long mid =(lo + hi + 1)/2;
            long t = 0;
            for(auto i : citations){
                if(i >= mid)t++;
            }
            if(t >= mid){
                ans = mid;
                lo = mid + 1;
            }else{
                hi = mid - 1;
            }
        }
        return ans;
    }
};
