class Solution {
public:
    int hIndex(vector<int>& citations) {
        sort(begin(citations), end(citations));
        int ans = 0;
        for(int i = 0; i <= 1000;i++){
            int index = lower_bound(begin(citations), end(citations), i) - citations.begin();
            if(i <= citations.size() - index){
                ans = max(ans, i);
            }
        }
        return ans;
    }
};
