class Solution {
public:
    bool isHappy(int n) {
        unordered_map<int, int> m;
        while(n != 1){
            cout<<n<<endl;
            if(m.find(n) != m.end())return false;
            m[n]++;
            int temp = n;
            int nn = 0;
            while(temp > 0){
                nn += (temp%10) * (temp%10);
                temp/=10;
            }
            n = nn;
        }
        return true;
    }
};
