class Solution {
public:
    int findRadius(vector<int>& houses, vector<int>& heaters) {
        sort(houses.begin(), houses.end());
        sort(heaters.begin(), heaters.end());
        int ans = -1e9;
        for(auto i : houses){
            auto it = lower_bound(heaters.begin(), heaters.end(), i);
            int mn = 1e9;
            if(it != heaters.end()){
                mn = min(mn,abs(*it - i));
            }
            if(it != heaters.begin()){
                mn = min(mn, abs(i - *(--it)));
            }
            ans = max(ans, mn);
        }
        return ans;
    }
};
