class Solution {
public:
    vector<int> smallerNumbersThanCurrent(vector<int>& nums) {
        vector<int> arr(101, 0);
        for(auto i : nums){
            arr[i] ++;
        }
        vector<int> ansArr;
        for(auto i : nums){
            int ans = 0;
            for(int j = 0; j<i;j++){
                ans += arr[j];
            }
            ansArr.push_back(ans);
        }
        return ansArr;
    }
};
