class Solution {
public:
    vector<vector<int>> imageSmoother(vector<vector<int>>& img) {
        int n = img.size(), m = img[0].size();
        vector<int> d4x = {-1,-1,-1,0,1,1,1, 0};
        vector<int> d4y = {-1,0,1,1,1,0,-1, -1};
        vector<vector<int>> ans = img;
        for(int i = 0; i < n;i++){
            for(int j= 0; j < m;j++){
                int cnt = 1;
                int sum = img[i][j];
                for(int k = 0; k < 8;k++){
                    int x = i + d4x[k];
                    int y = j + d4y[k];
                    if(x>=0 && x<n && y>=0 && y<m){
                        sum += img[x][y];
                        cnt++;
                    }
                }
                ans[i][j] = sum/cnt;
            }
        }
        return ans;
    }
};
