class MyQueue {
public:
    stack<int> a;
    MyQueue() {
    }

    void push(int x) {
        stack<int> b;
        while(!a.empty()){
            int top = a.top();
            a.pop();
            b.push(top);
        }
        b.push(x);
        while(!b.empty()){
            int top = b.top();
            b.pop();
            a.push(top);
        }
    }

    int pop() {
        int top = a.top();
        a.pop();
        return top;
    }

    int peek() {
        return a.top();
    }

    bool empty() {
        return a.empty();
    }
};

