class Solution {
public:
    int  mat[500][500];
vector<vector<int>> rangeAddQueries(int n, vector<vector<int>>& p) {
       vector<vector<int>>ans;
       for(int i=0;i<n;i++){
               for(int j=0;j<n;j++){
                       mat[i][j]=0;
               }
       }

     for(auto x:p){
           int a=x[0]; int b=x[1];
           int a1=x[2]; int b1=x[3];
           for(int i=a;i<=a1;i++){
               for(int j=b;j<=b1;j++){
                   mat[i][j]+=1;
               }
           }           
     }
     for(int i=0;i<n;i++){
         vector<int>v;
         for(int j=0;j<n;j++){
            v.push_back(mat[i][j]);
         }
         ans.push_back(v);
     }

    return ans;

}

};
