class RandomizedSet {
public:

    unordered_map<int, int> m;
    vector<int> arr;
    RandomizedSet() {

    }

    bool insert(int val) {
        if(!m.count(val)) {
            arr.push_back(val);
            m[val] = arr.size() - 1;
            return true;
        }
        return false;
    }

    bool remove(int val) {
        if(m.count(val)) {
            int indx = m[val];
            arr[indx] = arr.back();
            m[arr.back()] = indx;
            arr.pop_back();
            m.erase(val);
            return true;
        }
        return false;
    }

    int getRandom() {
        int indx = rand() % (arr.size());
        return arr[indx];
    }
};

