class Solution {
public:
    int dp[105][105][300];
    bool helper(string &a, string &b, string &s, int i, int j, int k){
        if(k == s.length() && i == a.length() && j == b.length()){
            return true;
        }
        if(dp[i][j][k] != -1)return dp[i][j][k];
        bool b1 = false;
        if(i < a.length() && a[i] == s[k]){
            b1  = b1 || helper(a, b, s, i + 1, j, k + 1);
        }

        if(j < b.length() && b[j] == s[k]){
            b1  = b1 || helper(a, b, s, i, j + 1, k + 1);
        }

        return dp[i][j][k] = b1;
    }
    bool isInterleave(string a, string b, string s) {
        memset(dp, -1, sizeof(dp));
        return helper(a, b, s, 0 , 0, 0);
    }
};
