class Solution {
public:
    bool helper(vector<vector<int>>& adj, vector<int> &color, int curr, int col){
        color[curr] = col;
        bool b = true;
        for(auto i : adj[curr]){
            if(color[i] == col)return false;
            if(color[i] != -1)continue;
            b &= helper(adj, color, i, 1 - col);
        }
        return b;
    }
    bool isBipartite(vector<vector<int>>& graph) {
        vector<int> color(graph.size(), -1);
        bool b = true;
        for(int i = 0; i < graph.size(); i++){
            if(color[i] == -1) b &= helper(graph, color, i, 0);
        }
        return b;
    }
};
