class Solution {
public:
    int islandPerimeter(vector<vector<int>>& grid) {
        int ans = 0;
        for(auto i : grid){
            for(auto j : i){
                if(j == 1)ans += 4;
            }
        }
        vector<int> arr = {-1, 0, 1, 0, -1};
        for(int i = 0; i <grid.size();i++){
            for(int j = 0; j < grid[0].size();j++){
                if(grid[i][j] == 0)continue;
                for(int k = 0; k < 4;k++){
                    int _x = i + arr[k];
                    int _y = j + arr[k + 1];
                    if(_x >= 0 && _x < grid.size() && _y >= 0 && _y < grid[0].size() && grid[_x][_y] == 1){
                        ans--;
                    }
                }
            }
        }
        return ans;
    }
};
