class Solution {
public:
    bool isIsomorphic(string s, string t) {
        if(s.length() != t.length()) return false;
        unordered_map<char, char> umap1;
        unordered_map<char, int> umap2;
        for(int i=0;i<t.length();i++){
            if(umap1.find(s[i]) == umap1.end()){
                if(umap2[t[i]] == 1){
                    return false;
                }
                umap1[s[i]] = t[i];
                umap2[t[i]] = 1;
                s[i] = t[i];
            }else{
                s[i] = umap1[s[i]];
            }
        }
        return s == t;
    }
};
