class Solution {
public:
    int numJewelsInStones(string jewels, string stones) {
        int ans = 0;
        unordered_map<char, int> umap;
        for(auto i : jewels){
            umap[i] = 1;
        }
        for(auto i  : stones){
            if(umap.find(i) != umap.end()) ans++;
        }
        return ans;

    }
};
