class Solution {
public:
    int jump(vector<int>& nums) {
        int n = nums.size();
        vector<int> dist(n, 1e9);
        dist[0] = 0;
        for(int i = 0; i < n; i ++){
            for(int j = i; j <= min(n - 1, i + nums[i]); j++){
                dist[j] = min(dist[j], 1 + dist[i]);
            }
        }
        return dist[n - 1];
    }
};
