class Solution {
public:
    bool canReach(string s, int minJump, int maxJump) {
        int maxJ = 0;
        for(int i = 0; i < s.size();i++){
            if(i == 0 || s[i] == '2'){
                for(int j = max(maxJ, i + minJump); j<= min(i + maxJump, (int)s.size() - 1);j++){
                    if(s[j] == '0'){
                        s[j] = '2';
                    }
                    maxJ = j;
                }
            }
        }
        return (s[s.size() - 1] == '2');
    }
};
