class Solution {
public:
    int dp[100005];
    bool helper(vector<int> &nums, int ind){
        if(ind >= nums.size()){
            return false;
        }
        if(ind == nums.size() - 1){
            return true;
        }
        if(dp[ind] != -1)return dp[ind];
        for(int i = 1; i<=nums[ind];i++){
            if(helper(nums, ind + i)){
                return true;
            }
        }
        return dp[ind] = false;
    }
    bool canJump(vector<int>& nums) {
        memset(dp, -1, sizeof dp);
        return helper(nums, 0);        
    }
};
