class Solution {
public:
    vector<vector<int>> kClosest(vector<vector<int>>& points, int k) {
        vector<vector<int>> v;
        multimap<int,vector<int>> m;
        for(auto i:points){
          m.insert(pair<int,vector<int>>(i[0]*i[0]+i[1]*i[1],{i[0],i[1]}));   
        }
        auto it= m.begin();
        for(int i =0;i<k and it!=m.end();i++){
            v.push_back(it->second);
            it++;
        }
        return v;
    }
};
