class Solution {
public:
    const long long MOD = 1000000007;

    long long kInversePairs(long long n, long long k) {
        long long dp[n + 5][k + 5];
        memset(dp, 0, sizeof dp);
        for(long long i = 0; i <= n; i++) {
            dp[i][0] = 1;
        }
        for(long long i = 0; i <= k; i++) {
            dp[0][i] = 0;
        }


        for(long long i = 1; i <= n; i++) {
            for(long long j = 1; j <= k ; j++) {
                    dp[i][j] = dp[i][j - 1] + (dp[i - 1][j] + MOD - ((j - i) >= 0 ? dp[i - 1][j - i]%MOD : 0))%MOD;
            }
        }
        return dp[n][k] - ((k - 1) >= 0 ? dp[n][k - 1] : 0);
    }
};
