class Solution {
public:
    int minEatingSpeed(vector<int>& piles, int h) {
        long long lo = 1, hi = 1e18;
        long long ans = hi;
        while(lo <= hi){
            long long mid = lo + (hi - lo)/2;
            long long hr = 0;
            for(auto i : piles){
                long long it = 1LL * i;
                long long temp = ceil((it * 1.0)/mid);
                hr += temp;
            }
            if(h >= hr){
                ans = mid;
                hi = mid - 1;
            }else{
                lo = mid + 1;
            }
        }
        return ans;
    }
};
