class Solution {
public:
    int findKthPositive(vector<int>& arr, int k) {
        vector<int> dp(1005, 0); 
        for(auto i : arr){
            dp[i]++;
        }
        for(int i = 1; i < 1005; i++){
            if(dp[i] == 0){
                k--;
                if(k == 0)
                {
                    return i;
                }
            }
        }
        return 1004 + k;
    }
};
