class Solution {
public:
    static bool cmp(pair<int,int>&x,pair<int,int>&y){
        return x.second>y.second;
    }
    void count_bitset(int num,vector<int>&count_vec){
        int count=0;
        for(int i=0;i<25;i++){
            count_vec[i]+=(((1<<i)&num)>0);
        }
    }
    int largestCombination(vector<int>& can) {
        vector<pair<int,int>> vec;
        vector<int> count_vec(25,0);
        for(auto i:can){
            count_bitset(i,count_vec);
        }
        int count=0;
        for(auto i:count_vec){
            count=max(count,i);
        }
        return count;
    }
};
