class Solution {
public:
    long long maxArrayValue(vector<int>& nums) {
        long long n = nums.size();
        long long prev = nums[n - 1], ans = 0; 
        for(int i = n - 2; i >= 0; i--){
            if (nums[i] <= prev){
                prev += nums[i];
            }else{
                ans = max(ans, prev);
                prev = nums[i];
            }
        }
         ans = max(ans, prev);
        return ans;
    }
};
