class Solution {
public:
    int largestInteger(int num) {
        vector<char> o, e;
        string s = to_string(num);
        for(auto i :  s)if((i-'0')%2 == 0)e.push_back(i);else o.push_back(i);
        sort(e.begin(), e.end(), greater<int>());
        sort(o.begin(), o.end(), greater<int>());
        int i= 0, j = 0;
        string ans = "";
        for(auto it : s){
            if((it-'0')% 2== 0){
                ans.push_back(e[i++]);
            }else{
                ans.push_back(o[j++]);
            }
        }
        return stoi(ans);
    }
};
